      selectedSub: null,
      selectedOption: null,
      businessID: "",
      businessDetailsResult: false,
      assessmentInvoice: false,
      fireInvoiceCreator: false,
      businessDetails: {
        id: "",
        businessID: "",
        businessName: "",
        subCountyID: "",
