        businessSubCategory: "",
        incomeTypeID: "",
        feeID: "",
        businessEmail: "",
        postalAddress: "",
        postalCode: "",
        businessPhone: "",
        contactPersonNames: "",
        contactPersonIDNo: "",
        businessRole: "",
        contactPersonPhone: "",
        contactPersonEmail: "",
        fullNames: "",
        ownerID: "",
        ownerPhone: "",
        ownerEmail: "",
        kraPin: "",
        createdBy: "",
        createdByIDNo: "",
        dateCreated: "",
        lat: 0.0,
        lng: 0.0
      },
      fireSafety: [],
      // Start with an empty array
      selectedItems: [],
      // Array to hold selected items
      recommendations: [] // Array to hold recommendation objects
    };
  },

  mounted() {
    this.selectedSub = sessionStorage.getItem("selectedSub");
    this.$watch('businessID', function (newValue) {
      this.businessID = newValue;
      this.getBusiness();
    });
    this.getFireMeasures();
  },
  methods: {
    getRight(type) {
      return getRight(type);
    },
    gotTo(route) {
      this.$router.push(route);
      sessionStorage.setItem("selectedSub", route);
      this.selectedSub = route;
    },
    getBusiness() {
      this.loading = true;
      this.message = null;
      this.businessDetailsResult = false;
      const data = new FormData();
      data.append("function", "getBusiness");
      data.append("businessID", this.businessID);
      execute(data, trade).then(res => {
        this.loading = false;
        if (res.data.success) {
          this.businessDetailsResult = true;
          this.businessDetails = res.data.data.business;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    getFireMeasures() {
      const data = new FormData();
      data.append("function", "getFireMeasures");
      execute(data, fire).then(res => {
        if (res.data.success) {
          // Add selected property to each item
          this.fireSafety = res.data.data.fireSafety.map(item => ({
            ...item,
            selected: false // Initialize the selected property
          }));
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    updateSelectedItems() {
      this.selectedItems = this.fireSafety.filter(item => item.selected);
    },
    addRecommendation() {
      this.recommendations.push({
        text: ''
      }); // Add a new recommendation with empty text
    },

    removeRecommendation(index) {
      this.recommendations.splice(index, 1); // Remove the recommendation at the specified index
    },

    submitRecommendations() {
      // Collect the recommendations into an array
      const submittedRecommendations = this.recommendations.map(r => r.text);
      console.log('Submitted Recommendations:', submittedRecommendations);
      // You can perform further actions here, e.g., sending data to an API
    }
  }
};
