  },
  computed: {},
  watch: {},
  mounted() {
    this.permission = sessionStorage.getItem("permission");
    this.getZonesData();
    this.menuAccess();
  },
  methods: {
    menuAccess() {
      const data = new FormData();
      data.append("function", "menuAccess");
      data.append("menu", "Zones");
      data.append("sessionNames", sessionStorage.getItem("sessionNames"));
      data.append("sessionIdNo", sessionStorage.getItem("sessionIdNo"));
      execute(data, biller).then(() => {
        // alert(res.data.message);
      }).catch(() => {
        // alert(e.message);
      });
    },
    getRight(type) {
      return getRight(type);
    },
    createZone(create) {
      this.message = null;
      this.updateRecord = false;
      if (!create) {
        this.zone = "";
        this.getMainStreams();
        this.getSubCounties();
        return;
      }
      this.message = null;
      if (this.latLng.split(',').length > 0) {
        this.lat = this.latLng.split(',')[0];
        this.lng = this.latLng.split(',')[1];
      }
      this.message = null;
      const data = new FormData();
      data.append("function", "createZone");
      data.append("subCountyID", this.subCountyID);
      data.append("zone", this.zone);
      data.append("lat", this.lat);
      data.append("lng", this.lng);
      data.append("subCountyID", this.subCounty.subCountyID);
      data.append("subCountyName", this.subCounty.subCountyName);
      data.append("wardID", this.ward.wardID);
      data.append("wardName", this.ward.wardName);
      data.append("stream", JSON.stringify(this.stream));
      execute(data, authUrl).then(res => {
        if (res.data.success) {
          this.getZones();
          this.zone = '';
          this.latLng = '';
          this.getZonesData();
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        //alert(e.message);
        this.message = e.message;
      });
    },
    updateZone() {
      this.message = null;
      if (this.latLng.split(',').length > 0) {
        this.lat = this.latLng.split(',')[0];
        this.lng = this.latLng.split(',')[1];
      }
      const data = new FormData();
      data.append("function", "updateZone");
      data.append("id", this.selectedZone.id);
      data.append("zone", this.zone);
      data.append("lat", this.lat);
      data.append("lng", this.lng);
      data.append("subCountyID", this.subCounty.subCountyID);
      data.append("subCountyName", this.subCounty.subCountyName);
      data.append("wardID", this.ward.wardID);
      data.append("stream", JSON.stringify(this.stream));
      execute(data, authUrl).then(res => {
        if (res.data.success) {
          this.getZonesData();
          this.getZones();
          this.zone = '';
          this.latLng = '';
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        //alert(e.message);
        this.message = e.message;
      });
    },
    zoneSelected(selectedZone, zoneCategory) {
      this.updateRecord = true;
      this.selectedZone = selectedZone;
      this.zoneCategory = zoneCategory;
      this.zone = selectedZone.zone;
      this.getSubCounties();
      this.getWards(this.selectedZone.subCountyID);
      this.getMainStreams();
    },
    deleteItem(selectedZone) {
      // Display a confirmation dialog
      const isConfirmed = window.confirm(`Are you sure you want to delete ${selectedZone.zone}?`);
      // Check the user's choice
      if (isConfirmed) {
        this.deleteZone(selectedZone.id);
      }
    },
    deleteZone(id) {
      this.message = null;
      const data = new FormData();
      data.append("function", "deleteZone");
      data.append("id", id);
      execute(data, biller).then(res => {
        if (res.data.success) {
          this.getZonesData();
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        //alert(e.message);
        this.message = e.message;
      });
    },
    getWards(subCountyID) {
      this.wards = [];
      this.message = null;
      const data = new FormData();
      data.append("function", "getWards");
      data.append("subCountyID", subCountyID);
      execute(data, authUrl).then(res => {
        if (res.data.success) {
          this.wards = res.data.data.wards;
          this.wards.forEach((value, index) => {
            if (this.selectedZone.wardID === value.wardID) {
              this.ward = this.wards[index];
            }
          });
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        //alert(e.message);
        this.message = e.message;
      });
    },
    getSubCounties() {
      this.message = null;
      this.subCounties.splice(0);
      this.message = null;
      const data = new FormData();
      data.append("function", "getSubCounties");
      data.append("countyId", "getSubCounties");
      execute(data, authUrl).then(res => {
        if (res.data.success) {
          this.subCounties = res.data.data.subCounties;

          //preselection
          this.subCounties.forEach((value, index) => {
            if (this.selectedZone.subCountyID === value.subCountyID) {
              this.subCounty = this.subCounties[index];
            }
          });
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        //alert(e.message);
        this.message = e.message;
      });
    },
    toRoman(num) {
      let result = '';
      let map = {
        M: 1000,
        CM: 900,
        D: 500,
        CD: 400,
        C: 100,
        XC: 90,
        L: 50,
        XL: 40,
        X: 10,
        IX: 9,
        V: 5,
        IV: 4,
        I: 1
      };
      for (let key in map) {
        while (num >= map[key]) {
          result += key;
          num -= map[key];
        }
      }
      return result;
    },
    toAlpha(num) {
      let alpha = "";
      while (num > 0) {
        let remainder = (num - 1) % 26;
        alpha = String.fromCharCode(65 + remainder) + alpha;
        num = Math.floor((num - 1) / 26);
      }
      return alpha;
    },
    getZonesData() {
      this.message = null;
      const data = new FormData();
      data.append("function", "getZonesData");
      execute(data, biller).then(res => {
        if (res.data.success) {
          this.zonesData.splice(0);
          this.zonesData = res.data.data;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        //alert(e.message);
        this.message = e.message;
      });
    },
    getMainStreams() {
      const data = new FormData();
      data.append("function", "getMainStreams");
      execute(data, biller).then(res => {
        if (res.data.success) {
          this.streams.splice(0);
          this.streams = res.data.data.streams;
          //preselection
          this.streams.forEach((value, index) => {
            // console.log(JSON.stringify(this.selectedZone))
            console.log(`${this.ZoneCategory} ::: ${value.stream}`);
            if (this.zoneCategory === value.stream) {
              this.stream = this.streams[index];
            }
          });
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        //alert(e.message);
        this.message = e.message;
      });
    }
  }
};
