import NavigationBar from "@/components/Navigation";
import ToolBar from "@/components/parking/ToolBar";
import { execute, executeDownload, parking } from "@/api";
export default {
  name: "DailyParking",
  components: {
    ToolBar,
    NavigationBar
  },
  data() {
    return {
      download: 'Download Report',
      numberPlate: '',
      search: '',
      saccoName: '',
      complianceStatus: '',
      totalAmount: 0,
      vehicles: [{
        id: "43975",
        parkingCode: "BILL010305404669",
        numberPlate: "KCR812Y",
        duration: "PER DAY",
        category: "Saloon Car",
        zone: "MERU TOWN PARKING",
        billTotal: "50",
        receiptAmount: "50",
        billBalance: "0",
        startDate: "2024-01-03 05:41:01",
        endDate: "2024-01-03 00:00:00",
        status: "PAID",
        transactionCode: "SA33X4822T",
        paymentMode: "MPESA",
        street: null,
        paidBy: "DAVID",
        saccoID: "",
        saccoName: "",
        feeID: "83834",
        dateCreated: "2024-01-03 02:35:28",
        subCountyName: null,
        subCountyID: null,
        wardName: null,
        wardID: null
      }],
      todayHighlights: [{
        title: "LOGGED VEHICLES",
        amount: "41,695"
      }]
    };
  },
  mounted() {
    $('.todays-entries').slick({
      arrows: true,
      prevArrow: '<button type="button" class="slick-prev"></button>',
      nextArrow: '<button type="button" class="slick-next"></button>',
      centerMode: true,
      slidesToShow: 7,
      speed: 7000,
      autoplay: true,
      autoplaySpeed: 0,
      cssEase: 'linear',
      slidesToScroll: 1,
      pauseOnFocus: true,
      variableWidth: true
    });
    this.saccoName = sessionStorage.getItem("saccoName");
    this.$watch('complianceStatus', function (newValue) {
      this.getSaccoVehiclesStatus();
    });
    this.$watch('search', function (newValue) {
      this.getSaccoVehiclesStatus();
    });
    this.saccoName = sessionStorage.getItem("saccoName");
    this.$watch('complianceStatus', function (newValue) {
      this.getSaccoVehiclesStatus();
    });
    this.$watch('search', function (newValue) {
      this.getSaccoVehiclesStatus();
    });
    this.getParkingEntries();
  },
  methods: {
    numberFormat(num) {
      const britishNumberFormatter = new Intl.NumberFormat("en-GB");
      return britishNumberFormatter.format(num);
    },
    getParkingEntries() {
      this.vehicles.splice(0);
      const data = new FormData();
      data.append("function", "getParkingEntries");
      execute(data, parking).then(res => {
        if (res.data.success) {
          this.vehicles = res.data.data.vehicles;
          this.totalAmount = this.vehicles.reduce((acc, cur) => {
            let amount = Number(cur.receiptAmount);
            if (!isNaN(amount)) {
              return acc + amount;
            } else {
              return acc;
            }
          }, 0);
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    getSaccoVehiclesStatus() {
      this.vehicles.splice(0);
      const data = new FormData();
      data.append("function", "getSaccoVehiclesStatus");
      data.append("compliance_status", this.complianceStatus);
      data.append("saccoID", sessionStorage.getItem('saccoID'));
      data.append("keyword", this.search);
      execute(data, parking).then(res => {
        if (res.data.success) {
          this.vehicles = res.data.data.vehicles;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    selectedVehicle(numberPlate) {
      this.numberPlate = numberPlate;
    },
    removeVehicle() {
      this.vehicles.splice(0);
      const data = new FormData();
      data.append("function", "removeVehicle");
      data.append("numberPlate", this.numberPlate);
      data.append("saccoID", sessionStorage.getItem('saccoID'));
      execute(data, parking).then(res => {
        if (res.data.success) {
          this.getSaccoVehiclesStatus();
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    downloadSaccoVehiclesStatus() {
      this.download = "Please wait..";
      const data = new FormData();
      data.append("function", "downloadSaccoVehiclesStatus");
      data.append("compliance_status", this.complianceStatus);
      data.append("saccoID", sessionStorage.getItem('saccoID'));
      data.append("keyword", this.search);
      executeDownload(data, parking).then(response => {
        this.download = "Download Report";
        const link = document.createElement('a');
        link.href = URL.createObjectURL(response.data);
        link.download = this.saccoName + "Report.csv";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      });
    }
  }
};
