      execute(data, health).then(res => {
        this.loading = false;
        if (res.data.success) {
          this.business = res.data.data.business;
          this.billDetail = res.data.data.bill.billDetails;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
