import "core-js/modules/es.array.push.js";
/* eslint-disable */
import NavigationBar from "@/components/Navigation.vue";
import { authUrl, biller, execute, execute2, getRight, plotRent, rent } from "@/api";
import router from "@/router";
import ToolBarHousing from "@/components/housing/ToolBarHousing.vue";
export default {
  name: "HousingNew",
  components: {
    ToolBarHousing,
    NavigationBar
  },
  data() {
    return {
      zone: '',
      zones: [],
      alert: {
        success: false,
        info: false,
        failed: false
      },
      incomeType: {
        incomeTypeId: null,
        incomeTypeDescription: null,
        incomeTypePrefix: null,
        fundDescription: null,
        status: null
      },
      feesAndCharges: [{
        feeId: null,
        feeDescription: null,
        unitOfMeasure: null,
        unitFeeAmount: null,
        accountNo: null,
        incomeTypeId: null,
        feeType: null,
        accountDesc: null,
        costCenterNo: null,
        zone: null,
        typeDescription: null
      }],
      incomeTypes: [{
        incomeTypeId: null,
        incomeTypeDescription: null,
        incomeTypePrefix: null,
        fundDescription: null,
        status: null
      }],
      message: null,
      download: 'Download Report',
      loading: false,
      dateFrom: '',
      dateTo: '',
      property: {
        property: null,
        propertyID: null,
        subCounty: {
          subCountyID: null,
          subCountyName: null
        },
        ward: {
          wardID: null,
          wardName: null
        },
        zone: null,
        units: [{
          unitNo: null,
          category: null,
          occupants: null,
          monthlyRent: null,
          block: null,
          arrears: null,
          phoneNumber: null,
          idNo: null
        }]
      },
      subCounties: [{
        subCountyID: "",
        subCountyName: ""
      }],
      wards: [{
        wardID: "",
        wardName: ""
      }],
      subCounty: {},
      ward: {},
      unitCategories: [{
        id: null,
        category: null
      }]
    };
  },
  computed: {
    totalReceiptAmount() {
      return this.formatPrice(this.receiptDetails.reduce((sum, billDetail) => sum + parseInt(billDetail.receiptAmount), 0));
    }
  },
  watch: {
    subCounty: function () {
      //Trade demographics
      this.property.subCounty = this.subCounty;
      this.getWards();
    },
    ward: function () {
      //Trade demographics
      this.property.ward = this.ward;
      this.getZones(this.ward.wardID);
    },
    zone: function () {
      this.property.zone = this.zone;
    }
  },
  mounted() {
    this.getProperty();
    // this.getSubCounties()
    this.getIncomeTypes();
    this.getUnitCategory();
  },
  methods: {
    getRight(type) {
      return getRight(type);
    },
    addUnits() {
      this.property.units.push({});
    },
    removeUnits(position) {
      this.property.units.splice(position, 1);
    },
    selectedIncomeType(event) {
      const value = event.target.value;
      const result = JSON.parse(value);
      this.getFeesAndChargesBilling(result.incomeTypeId);
    },
    getProperty() {
      const data = new FormData();
      data.append("function", "getProperty");
      data.append("propertyID", sessionStorage.getItem("propertyID"));
      execute(data, rent).then(res => {
        if (res.data.success) {
          this.property.property = res.data.data.property.property;
          this.property.propertyID = res.data.data.property.propertyID;
          this.property.subCounty.subCountyName = res.data.data.property.subCountyName;
          this.property.subCounty.subCountyID = res.data.data.property.subCountyID;
          this.property.ward.wardName = res.data.data.property.wardName;
          this.property.ward.wardID = res.data.data.property.wardID;
          this.property.zone = res.data.data.property.zone;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    getIncomeTypes() {
      const data = new FormData();
      data.append("function", "getIncomeTypes");
      data.append("incomeTypePrefix", "PKN");
      execute(data, biller).then(res => {
        if (res.data.success) {
          this.incomeTypes = res.data.data.incomeTypes;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    getFeesAndChargesBilling(incomeTypeId) {
      const data = new FormData();
      data.append("function", "getFeesAndCharges");
      data.append("incomeTypeId", incomeTypeId);
      execute(data, biller).then(res => {
        if (res.data.success) {
          this.feesAndCharges = res.data.data.feesAndCharges;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    handleFileChange: function (event) {
      console.log("File selected:", event.target.files[0]);
      this.selectedFile = event.target.files[0];
      this.uploadStallData();
    },
    uploadStallData() {
      if (!this.property.subCounty || !this.property.subCounty.subCountyName) {
        // Handle the case where subCounty or subCountyName is empty or null
        alert("Select Sub-county");
        return;
      }
      if (!this.property.ward || !this.property.ward.wardID) {
        // Handle the case where subCounty or subCountyName is empty or null
        alert("Select Ward ");
        return;
      }
      if (!this.property.zone) {
        // Handle the case where subCounty or subCountyName is empty or null
        alert("Select Zone");
        return;
      }
      this.property.units.splice(0);
      const data = new FormData();
      data.append("function", "uploadStallData");
      data.append("fileToUpload", this.selectedFile);
      data.append("zone", this.zone);
      data.append("createdBy", sessionStorage.getItem("sessionNames"));
      data.append("createdByIDNo", sessionStorage.getItem("sessionIdNo"));
      execute2(data, rent).then(res => {
        if (res.data.success) {
          this.property.units = res.data.data.units;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        // Handle network or other errors
        // alert(e.message);
        // this.alert.failed = true
      });
    },
    updateProperty() {
      this.message = "Please wait...";
      this.alert.failed = false;
      this.alert.info = true;
      const data = new FormData();
      data.append("function", "updateProperty");
      data.append("property", JSON.stringify(this.property));
      execute(data, rent).then(res => {
        if (res.data.success) {
          alert(res.data.message);
          this.gotTo('housing-register');
        } else {
          this.alert.failed = true;
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    //#########
    getUnitCategory() {
      this.unitCategories.splice(0);
      const data = new FormData();
      data.append("function", "getUnitCategory");
      execute(data, rent).then(res => {
        if (res.data.success) {
          this.unitCategories = res.data.data.unitCategories;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    getSubCounties() {
      const data = new FormData();
      data.append("function", "getSubCounty");
      execute(data, biller).then(res => {
        if (res.data.success) {
          this.subCounties = res.data.data.subCounties;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    getWards() {
      this.wards = [];
      const data = new FormData();
      data.append("function", "getWards");
      data.append("subCountyID", this.subCounty.subCountyID);
      execute(data, biller).then(res => {
        if (res.data.success) {
          this.wards = res.data.data.wards;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        this.message = e.message;
      });
    },
    getZones(wardID) {
      this.zones.splice(0);
      const data = new FormData();
      data.append("function", "getZones");
      data.append("wardID", wardID);
      execute(data, authUrl).then(res => {
        if (res.data.success) {
          this.zones = res.data.data.zones;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        //alert(e.message);
        this.message = e.message;
      });
    },
    getCurrentMonth() {
      const d = new Date();
      const monthName = this.monthNames[d.getMonth()];
      console.log(monthName);
      return monthName;
    },
    getMonthName() {
      const monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
      const d = new Date();
      const monthName = monthNames[d.getMonth()];
      console.log(monthName);
      return monthName;
    },
    getCurrentYear() {
      const date = new Date();
      const currentYear = date.getFullYear();
      console.log(currentYear); // Output: 2023
      return currentYear;
    },
    formatPrice(value) {
      var formatter = new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'KES',
        minimumFractionDigits: 2
      });
      return formatter.format(value);
    },
    gotTo(route) {
      router.push(route);
    }
  }
};
