import {createWebHashHistory, createRouter} from "vue-router";
import Login from "@/components/Login.vue";
import Dashboard from "@/components/Dashboard.vue";

import ReportsAgents from "@/components/reports/agents/ReportsAgents";


import store from "./store";

// add a beforeEach hook to your router instance
router.beforeEach((to, from, next) => {
// write your logic here
// for example, check if the user is logged in or not before accessing a protected route
    if (!to.meta.public) {
        if (store.getters.isLoggedIn) {
            next();
        } else {
            next("/");
        }
    } else {
        next();
    }
});


const routes =[

    {
        path:"/",
        name:"login",
        component: Login,
        meta:{public: true}
    },
    {
        path:"/dashboard",
        name:"dashboard",
        component: Dashboard,
        meta:{public: false}
    },

    {
        path:"/reports-agents",
        name:"reports-agents",
        component: ReportsAgents,
        meta:{public: true}
    }
]
const router = createRouter({
    history: createWebHashHistory(),
    routes
});

export default router;
